onClick:
{{
    (function () {
        LoginAPI.run(() => {
            if (LoginAPI.data && LoginAPI.data['2fa']) {
                storeValue('2fa', LoginAPI.data['2fa'], false);
                showModal('TFA_Modal');
            } else if (LoginAPI.data && LoginAPI.data["auth"]) {
                var accessKey = LoginAPI.data["auth"]["access_key"];
                var secretKey = LoginAPI.data["auth"]["secret_key"];
                storeValue('accessKey', accessKey);
                storeValue('secretKey', secretKey);
                navigateTo('Admin Access');
            }
        }, (e) => {
            showAlert(e.message, 'error');
            navigateTo('LoginPage');
        });
        resetWidget('emailaddress_input');
        resetWidget('password_input');
    })()
}}
